import React, {Component} from 'react';
import Profile from './Profile';
import nba from '../nba-client';
class Main extends Component {
    // Constructor 写法
    constructor() {
        super();
        this.state = {
            playerId: 201939,
            playerInfo: {}
        }
    }
    /*
    state = {
        playerInfo: {},
        playerId: 201939
    }
     */

    componentDidMount() {
        // 将nba放进全局
        window.nba = nba;
        console.log('curry -> ',  nba.findPlayer('Stephen Curry'))
        nba.stats.playerInfo({ PlayerID: nba.findPlayer('Stephen Curry').playerId})
            .then((info) => {
                console.log(info);
                const playInfo = Object.assign(info.commonPlayerInfo[0], info.playerHeadlineStats[0]);
                console.log(playInfo);
                this.setState({ playerInfo: playInfo });
            })
    }

    render() {
        return (
            <div className="main">
                <Profile playerInfo={this.state.playerInfo}/>
            </div>
        );
    }
}

export default Main;
